import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        while (t -- > 0) {            int n = sc.nextInt();            int k = Integer.highestOneBit(n - 1);            boolean first = true;            for (int i = k - 1; i >= 0; i --) {                if (!first) {                    System.out.print(" ");                }                System.out.print(i);                first = false;            }            for (int i = k; i <= n - 1; i ++) {                if (!first) {                    System.out.print(" ");                }                System.out.print(i);                first = false;            }            System.out.println();        }        sc.close();    }}