import java.util.*;public class Main {    static class P {        int v, i;        P(int v, int i) {            this.v = v;            this.i = i;        }    }    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        while (t -- > 0) {            int n = sc.nextInt();            P[] A = new P[n];            P[] B = new P[n];            P[] C = new P[n];            for (int i = 0; i < n; i ++) {                A[i] = new P(sc.nextInt(), i);            }            for (int i = 0; i < n; i ++) {                B[i] = new P(sc.nextInt(), i);            }            for (int i = 0; i < n; i ++) {                C[i] = new P(sc.nextInt(), i);            }            Arrays.sort(A, (x, y) -> Integer.compare(y.v, x.v));            Arrays.sort(B, (x, y) -> Integer.compare(y.v, x.v));            Arrays.sort(C, (x, y) -> Integer.compare(y.v, x.v));            int lim = Math.min(3, n);            long ans = 0;            for (int i = 0; i < lim; i ++) {                for (int j = 0; j < lim; j ++) {                    for (int k = 0; k < lim; k ++) {                        int ia = A[i].i, ib = B[j].i, ic = C[k].i;                        if (ia != ib && ia != ic && ib != ic) {                            long sum = (long) A[i].v + B[j].v + C[k].v;                            if (sum > ans) {                                ans = sum;                            }                        }                    }                }            }            System.out.println(ans);        }        sc.close();    }}