import java.io.*;public class Main{    public static void main(String[] args) throws Exception{        FastScanner fs = new FastScanner();        StringBuilder sb = new StringBuilder();        int t = fs.nextInt();        while(t-- > 0){            int n = fs.nextInt();            for(int i = 0; i < n; i++){                int p = fs.nextInt();                if(i > 0) sb.append(' ');                sb.append(n + 1 - p);            }            sb.append('\n');        }        System.out.print(sb.toString());    }    static class FastScanner{        private final BufferedInputStream in = new BufferedInputStream(System.in);        private final byte[] buffer = new byte[1 << 16];        private int ptr = 0, len = 0;        private int read() throws Exception{            if(ptr >= len){                len = in.read(buffer);                ptr = 0;                if(len <= 0) return -1;            }            return buffer[ptr++];        }        int nextInt() throws Exception{            int c;            while((c = read()) <= 32){                if(c == -1) return Integer.MIN_VALUE;            }            int sign = 1;            if(c == '-') { sign = -1; c = read(); }            int val = 0;            while(c > 32){                val = val * 10 + (c - '0');                c = read();            }            return val * sign;        }    }}