import java.io.*;import java.util.*;public class CutArrayB{ public static void main(String[] args) throws Exception{  BufferedReader br=new BufferedReader(new InputStreamReader(System.in));  int t=Integer.parseInt(br.readLine().trim());  StringBuilder sb=new StringBuilder();  while(t-->0){   int n=Integer.parseInt(br.readLine().trim());   StringTokenizer st=new StringTokenizer(br.readLine());   int[] a=new int[n+1];   for(int i=1;i<=n;i++) a[i]=Integer.parseInt(st.nextToken());   int[] p=new int[n+1];   for(int i=1;i<=n;i++) p[i]=(p[i-1]+a[i])%3;   int tot=p[n];   if(tot%3!=0){    sb.append("0 0\n");    continue;   }   int[] first=new int[3];   int[] last=new int[3];   Arrays.fill(first,-1);   Arrays.fill(last,-1);   for(int i=1;i<=n-1;i++){    int m=p[i];    if(first[m]==-1) first[m]=i;    last[m]=i;   }   boolean ok=false;   for(int x=0;x<3 && !ok;x++){    for(int y=0;y<3 && !ok;y++){     int l=first[x];     int r=last[y];     if(l!=-1 && r!=-1 && l<r){      int s1=x;      int s2=(y-x+3)%3;      int s3=(tot-y+3)%3;      if((s1==s2 && s2==s3) || (s1!=s2 && s1!=s3 && s2!=s3)){       sb.append(l).append(' ').append(r).append('\n');       ok=true;      }     }    }   }   if(!ok) sb.append("0 0\n");  }  System.out.print(sb.toString()); }}