import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        while (t-- > 0) {            int n = sc.nextInt();            long target = sc.nextLong();            long step = sc.nextLong();            long total = 0;            for (int i = 0; i < n; i++) {                total += sc.nextLong();            }            while (total < target) {                total += step;            }            System.out.println(total == target ? "YES" : "NO");        }        sc.close();    }}