import java.util.*;public class Main {    static int oddPart(int x) {        while ((x & 1) == 0) x >>= 1;        return x;    }    public static void main(String[] args) {        Scanner fs = new Scanner(System.in);        int t = fs.nextInt();        while (t-- > 0) {            int n = fs.nextInt();            boolean ok = true;            for (int i = 1; i <= n; i++) {                int v = fs.nextInt();                if (oddPart(v) != oddPart(i)) ok = false;            }            System.out.println(ok ? "YES" : "NO");        }    }}