import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        while (t -- > 0) {            long x = sc.nextLong();            long y = sc.nextLong();            if (reach(x,y)) {                System.out.println("YES");            }            else {                System.out.println("NO");            }        }    }    public static boolean reach(long x, long y) {        long u = x - 2L * y;        if (u < 0) {            return false;        }        if (u % 3 != 0) {            return false;        }        long need = y < 0 ? -y : 0;        long max = u / 6;        if (need > max) {            return false;        }        long rem = u - 6L * need;        return rem >= 0 && rem % 3 == 0;    }}