import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        while (t -- > 0) {            int n = sc.nextInt();            long[] arr = new long[n];            int s = 0, ans = 0;            for (int i = 0; i < n; i ++) {                long v = sc.nextLong();                long need = v - 1;                while (s > 0 && arr[s - 1] != need) {                    s --;                }                if (s == 0) {                    ans ++;                }                arr[s ++] = v;            }            System.out.println(ans);        }    }}