import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        while (t -- > 0) {            int n = sc.nextInt();            int[] arr = new int[n];            int pos = -1;            for (int i = 0; i < n; i ++) {                arr[i] = sc.nextInt();                if (arr[i] == n) {                    pos = i;                }            }            int mx = 0;            int first = -1;            int ugly = 0;            for (int i = 0; i < n; i++) {                if (arr[i] > mx) {                    mx = arr[i];                }                if (mx == i + 1) {                    ugly ++;                    if (i != n - 1 && first == -1) {                        first = i;                    }                }            }            if (n > 1 && ugly > 1) {                int tmp = arr[first];                arr[first] = arr[pos];                arr[pos] = tmp;            }            for (int i = 0; i < n; i++) {                if (i > 0) {                    System.out.print(' ');                }                System.out.print(arr[i]);            }            if (t > 0) {                System.out.println();            }        }    }}