import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int[] primes = p(31623);        int t = sc.nextInt();        while (t -- > 0) {            int n = sc.nextInt();            long ans = 1;            for (int x : primes) {                if ((long) x * x > n) {                    break;                }                if (n % x == 0) {                    ans *= x;                    while (n % x == 0) {                        n /= x;                    }                }            }            if (n > 1) {                ans *= n;            }            System.out.println(ans);        }    }    public static int[] p(int n) {        boolean[] c = new boolean[n + 1];        int[] tmp = new int[n + 1];        int cnt = 0;        for (int i = 2; i <= n; i++) {            if (!c[i]) {                tmp[cnt++] = i;                if ((long) i * i <= n) {                    for (int j = i * i; j <= n; j += i) {                        c[j] = true;                    }                }            }        }        return Arrays.copyOf(tmp, cnt);    }}